function resetMargins() { 
	$('.no-touch #site-logo-, .no-touch #ContentOuter, .no-touch .collection_top').css('margin-top', $('#Header').height() + 20); 
	$('.no-touch .collection_top').css('margin-bottom', -($('#Header').height() + 20)); 

	$('#Header').animate({ opacity: 1 }, 300, function() { 
		$('#MainNavOuter, #ContentOuter, .collection_top').animate({ opacity: 1 }, 300, function() { 
			$('#Footer').animate({ opacity: 1 }, 300);  
			$('.index #MainNav li').children('ul').delay(400).slideDown('slow', 'easeInOutQuad');
			$('.img_outer .sale_marker').animate({ opacity: 1, bottom: 0 }, 500); 
		}); 
	}); 

	var didScroll = false;

	$(window).scroll(function() { 
		didScroll = true;

	}); 

	setInterval(function() {
		if(didScroll) {
			didScroll = false; 
			if($(window).scrollTop() > 20) { 
				$('#Header').addClass('scrolled'); 
			} else { 
				$('#Header').removeClass('scrolled'); 
			}
		}
	}, 100);
}

function ieSliderResize() { 
	$('.ie9 #HomeSlides img, .no-rgba #HomeSlides img').css('min-width', $(window).width()).css('min-height', $(window).height()); 
}

function homeSlideCenter() { 
	$('.no-rgba #HomeSlides .slides li').width($(window).width()); 


	$('.no-rgba #HomeSlides .slides img').each(function() { 
		if($(this).width() > $(window).width()) { 
			var newImgM = parseInt(0 - ($(this).width() - $(window).width())/2); 
			$(this).css('margin-left', newImgM); 
		} else { 
			$(this).css('margin-left', 0);  
		}

		if($(this).height() > $(window).height()) { 
			var newImgM = parseInt(0 - ($(this).height() - $(window).height())/2); 
			$(this).css('margin-top', newImgM); 
		} else { 
			$(this).css('margin-top', 0);  
		}
	}); 

}


// store the slider in a local variable
var $window = $(window),
		flexslider;

// tiny helper function to add breakpoints
function getGridSize() {
	return (window.innerWidth < 380) ? 3 :
	(window.innerWidth < 750) ? 5 : 5;
}

function readDeviceOrientation() {         		
	if (Math.abs(window.orientation) === 90) {
		// Landscape
		var origsrc = $('#MobileHomeSlides img').attr('src');
		var src = $('#HomeSlides img').attr('src');
		$("#MobileHomeSlides img").attr("src",src);
		$("#MobileHomeSlides img").attr("data-orig",origsrc);        
		$('#MobileHomeSlides .slides li').width(getWindowWidth() + "px");
		$('html, body').animate({scrollTop: '0px'}, 0);
	} else {
		// Portrait
		var origsrc = $('#MobileHomeSlides img').attr('data-orig');
		$("#MobileHomeSlides img").attr("src",origsrc);
		$('#MobileHomeSlides .slides li').width($(window).width());
		$('html, body').animate({scrollTop: '0px'}, 0);
	}
}

function getWindowWidth() {
	var windowWidth = 0;
	if (typeof(window.innerWidth) == 'number') {
		windowWidth = window.innerWidth;
	}
	else {
		if (document.documentElement && document.documentElement.clientWidth) {
			windowWidth = document.documentElement.clientWidth;
		}
		else {
			if (document.body && document.body.clientWidth) {
				windowWidth = document.body.clientWidth;
			}
		}
	}
	return windowWidth;
}

