$(window).resize(function() { 
	homeSlideCenter(); 

	if($(window).width() <= 767) { 
		$('body').addClass('mobile'); 
		$('ul.filter_by_tag').css('display', 'none'); 
		$('#Header').removeClass('scrolled'); 

	} else { 
		$('body').removeClass('mobile'); 
		$('#MainNavOuter').show(); 
		$('ul.filter_by_tag').css('display', 'inline'); 
		$('#Header').css('opacity', 1); 
		resetMargins(); 
	}
	ieSliderResize(); 
}); 



